mapper.insert:
INSERT INTO checklist_db.progress (tid, uid, cid, mark)
VALUES (#{tid}, #{uid}, #{cid}, #{mark})
ON DUPLICATE KEY UPDATE mark = VALUES(mark);

INSERT INTO checklist_db.progress (tid, uid, cid)
VALUES (1, #{uid}, #{cid})
ON DUPLICATE KEY UPDATE mark = VALUES(mark);

mapper.update:
update checklist_db.progress
set tid =#{tid}
where id = #{id};

mapper.insert:
INSERT INTO checklist_db.progress (tid, uid, cid, mark)
VALUES (#{tid}, #{uid}, #{cid}, #{mark})
ON DUPLICATE KEY UPDATE uid = VALUES(uid);

mapper.delete:
DELETE
FROM checklist_db.progress
WHERE tid = #{tid}
  AND cid = #{cid}
  AND uid = #{uid};

mapper.select:
select *
from checklist_db.progress
where tid = #{tid}
  and uid = #{uid}
  and cid = #{cid};

select *
from checklist_db.progress
where tid = #{tid}
limit 1;

select *
from checklist_db.progress
where uid = #{uid}
  and cid = #{cid}
  and tid != 0
limit 1;
